/**
 * 创建一个取值函数，该函数能够获取目标 object 上指定 path 的值
 *
 * @param {Array|string} path 路径信息
 * @returns {Function} 取值函数
 * @example
 *
 * const objects = [
 *   { 'a': { 'b': 2 } },
 *   { 'a': { 'b': 1 } }
 * ]
 *
 * map(objects, property('a.b'))
 * // => [2, 1]
 *
 * map(sortBy(objects, property(['a', 'b'])), 'a.b')
 * // => [1, 2]
 */
export default function property(path: Array<any> | string): (object?: any) => any;
