import { countryEnum } from '@ohos/hvigor-common';
export type MoreInfo = Record<countryEnum, string>;
/**
 * json文件里的错误信息
 */
export interface BaseErrorInfo {
    code?: string;
    moreInfo?: MoreInfo;
    checkMessage?: string;
    solutions?: string[];
}
/**
 * json文件里的错误信息
 * hvigor及插件
 */
export interface HvigorErrorInfo extends BaseErrorInfo {
    message?: string;
}
/**
 * json文件里的错误信息
 * 三方工具
 */
export interface ThirdPartyErrorInfo extends BaseErrorInfo {
    toolName?: string;
}
export interface ErrorInfoId {
    id: string;
}
/**
 * 支持匹配错误信息的字段
 */
export type MatchFieldType = 'none' | 'id' | 'code' | 'checkMessage';
/**
 * 在json查找错误信息用的筛选项
 * field只能是错误信息里的key
 */
export interface MatchOptions {
    field: MatchFieldType;
    value: any;
}
/**
 * 适配器返回的错误信息
 */
export interface AdaptorErrorMessage {
    timestamp?: Date;
    id?: string;
    code?: string;
    originMessage?: string;
    originSolutions?: string[];
    moreInfo?: string;
    stack?: string;
    message: string;
    solutions?: string[];
    components?: string;
    checkMessage?: string;
}
